import React, { useState, useEffect } from 'react';
import './App.css';

interface Entry {
  name: string;
  number: string;
  email: string;
  amountToPay: number;
  amountPaid: number;
}

const API_URL = 'http://localhost:4000/api/customers';

function App() {
  const [form, setForm] = useState({
    name: '',
    number: '',
    email: '',
    amountToPay: '',
    amountPaid: '',
  });
  const [entries, setEntries] = useState<Entry[]>([]);
  const [loading, setLoading] = useState(false);

  const fetchEntries = async () => {
    setLoading(true);
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setEntries(data);
    } catch (err) {
      // handle error
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchEntries();
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const payload = {
      name: form.name,
      number: form.number,
      email: form.email,
      amountToPay: parseFloat(form.amountToPay) || 0,
      amountPaid: parseFloat(form.amountPaid) || 0,
    };
    await fetch(API_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload),
    });
    setForm({ name: '', number: '', email: '', amountToPay: '', amountPaid: '' });
    fetchEntries();
  };

  return (
    <div className="dashboard-container">
      <h1>Duedesk Dashboard</h1>
      <form className="entry-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="number"
          placeholder="Number"
          value={form.number}
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="amountToPay"
          placeholder="Amount to Pay"
          value={form.amountToPay}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="amountPaid"
          placeholder="Amount Paid"
          value={form.amountPaid}
          onChange={handleChange}
          required
        />
        <button type="submit">Add/Update Entry</button>
      </form>
      <div className="table-container">
        {loading ? (
          <p>Loading...</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Number</th>
                <th>Email</th>
                <th>Amount to Pay</th>
                <th>Amount Paid</th>
                <th>Pending Amount</th>
              </tr>
            </thead>
            <tbody>
              {entries.map((entry, idx) => (
                <tr key={idx}>
                  <td>{entry.name}</td>
                  <td>{entry.number}</td>
                  <td>{entry.email}</td>
                  <td>{entry.amountToPay}</td>
                  <td>{entry.amountPaid}</td>
                  <td>{entry.amountToPay - entry.amountPaid}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default App;
