                </button>
              </div>

              {/* Tab Content */}
              <div className="tab-content">
                {/* Overview Tab */}
                {activeDetailsTab === 'overview' && (
                  <div className="tab-pane overview">
                    <div className="customer-info-section">
                      <div className="info-grid">
                        <div className="info-item">
                          <label>ðŸ‘¤ Customer Name:</label>
