import React, { useState, useEffect, useMemo } from 'react';
import './App.css';

interface Entry {
  id?: number;
  name: string;
  number: string;
  email: string;
  amountToPay: number;
  amountPaid: number;
}

const API_URL = 'http://localhost:4000/api/customers';

function App() {
  const [form, setForm] = useState({
    name: '',
    number: '',
    email: '',
    amountToPay: '',
    amountPaid: '',
  });
  const [entries, setEntries] = useState<Entry[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string>('');
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [successMessage, setSuccessMessage] = useState<string>('');
  const [editingEntry, setEditingEntry] = useState<Entry | null>(null);
  const [isEditMode, setIsEditMode] = useState(false);

  const fetchEntries = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await fetch(API_URL);
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      const data = await res.json();
      setEntries(data);
    } catch (err) {
      setError('Failed to fetch customers. Please check if the server is running.');
      console.error('Fetch error:', err);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchEntries();
  }, []);

  // Filter entries based on search term
  const filteredEntries = useMemo(() => {
    if (!searchTerm) return entries;
    return entries.filter(entry =>
      entry.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      entry.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      entry.number.includes(searchTerm)
    );
  }, [entries, searchTerm]);

  // Calculate summary statistics
  const summary = useMemo(() => {
    const totalAmountToPay = entries.reduce((sum, entry) => sum + entry.amountToPay, 0);
    const totalAmountPaid = entries.reduce((sum, entry) => sum + entry.amountPaid, 0);
    const totalPending = totalAmountToPay - totalAmountPaid;
    const customersCount = entries.length;
    const fullyPaidCount = entries.filter(entry => entry.amountToPay <= entry.amountPaid).length;
    const pendingCount = entries.filter(entry => entry.amountToPay > entry.amountPaid).length;

    return {
      totalAmountToPay,
      totalAmountPaid,
      totalPending,
      customersCount,
      fullyPaidCount,
      pendingCount
    };
  }, [entries]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    // Client-side validation
    if (parseFloat(form.amountToPay) < 0 || parseFloat(form.amountPaid) < 0) {
      setError('Amounts cannot be negative');
      return;
    }

    try {
      const payload = {
        name: form.name.trim(),
        number: form.number.trim(),
        email: form.email.trim(),
        amountToPay: parseFloat(form.amountToPay) || 0,
        amountPaid: parseFloat(form.amountPaid) || 0,
      };

      const res = await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const errorData = await res.json();
        throw new Error(errorData.error || `HTTP error! status: ${res.status}`);
      }

      const result = await res.json();
      clearForm();
      setSuccessMessage(isEditMode ? 'Customer updated successfully!' : 'Customer added successfully!');
      setIsFormVisible(false);
      fetchEntries();

      // Clear success message after 3 seconds
      setTimeout(() => setSuccessMessage(''), 3000);
    } catch (err) {
      setError('Failed to save customer: ' + (err instanceof Error ? err.message : 'Unknown error'));
      console.error('Submit error:', err);
    }
  };

  const clearForm = () => {
    setForm({ name: '', number: '', email: '', amountToPay: '', amountPaid: '' });
    setError('');
    setSuccessMessage('');
    setEditingEntry(null);
    setIsEditMode(false);
  };

  const handleEdit = (entry: Entry) => {
    setEditingEntry(entry);
    setIsEditMode(true);
    setForm({
      name: entry.name,
      number: entry.number,
      email: entry.email,
      amountToPay: entry.amountToPay.toString(),
      amountPaid: entry.amountPaid.toString(),
    });
    setIsFormVisible(true);
    setError('');
    setSuccessMessage('');
  };

  const cancelEdit = () => {
    setEditingEntry(null);
    setIsEditMode(false);
    clearForm();
    setIsFormVisible(false);
  };

  const handleAddNew = () => {
    if (isEditMode) {
      cancelEdit();
    }
    setIsFormVisible(!isFormVisible);
  };

  const formatCurrency = (amount: number) => {
    return `‚Çπ${amount.toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;
  };

  const getStatusBadge = (amountToPay: number, amountPaid: number) => {
    const pending = amountToPay - amountPaid;
    if (pending <= 0) {
      return <span className="status-badge paid">Paid</span>;
    } else if (amountPaid > 0) {
      return <span className="status-badge partial">Partial</span>;
    } else {
      return <span className="status-badge pending">Pending</span>;
    }
  };

  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div className="header-content">
          <h1>üíº DueDesk Dashboard</h1>
          <p>Manage customer payments with ease</p>
        </div>
      </header>

      <div className="main-layout">
        {/* Sidebar */}
        <aside className="sidebar">
          <div className="summary-card">
            <h3>üìä Summary</h3>
            <div className="summary-item">
              <span className="summary-label">Total Customers:</span>
              <span className="summary-value">{summary.customersCount}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Fully Paid:</span>
              <span className="summary-value paid">{summary.fullyPaidCount}</span>
            </div>
            <div className="summary-item">
              <span className="summary-label">Pending:</span>
              <span className="summary-value pending">{summary.pendingCount}</span>
            </div>
            <hr className="summary-divider" />
            <div className="summary-item total">
              <span className="summary-label">Total to Collect:</span>
              <span className="summary-value">{formatCurrency(summary.totalAmountToPay)}</span>
            </div>
            <div className="summary-item total">
              <span className="summary-label">Total Collected:</span>
              <span className="summary-value">{formatCurrency(summary.totalAmountPaid)}</span>
            </div>
            <div className="summary-item highlight">
              <span className="summary-label">üí∞ Amount Pending:</span>
              <span className="summary-value pending-amount">{formatCurrency(summary.totalPending)}</span>
            </div>
          </div>
        </aside>

        {/* Main Content */}
        <main className="main-content">
          {/* Action Bar */}
          <div className="action-bar">
            <div className="search-container">
              <input
                type="text"
                placeholder="üîç Search by name, email, or phone number..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
              {searchTerm && (
                <button 
                  onClick={() => setSearchTerm('')}
                  className="clear-search"
                  title="Clear search"
                >
                  ‚úï
                </button>
              )}
            </div>
            <button 
              onClick={handleAddNew}
              className="add-customer-btn"
            >
              {isFormVisible ? (isEditMode ? '‚ùå Cancel Edit' : '‚ùå Cancel') : '‚ûï Add Customer'}
            </button>
          </div>

          {/* Messages */}
          {error && (
            <div className="message error-message">
              ‚ö†Ô∏è {error}
              <button onClick={() => setError('')} className="close-message">‚úï</button>
            </div>
          )}

          {successMessage && (
            <div className="message success-message">
              ‚úÖ {successMessage}
              <button onClick={() => setSuccessMessage('')} className="close-message">‚úï</button>
            </div>
          )}

          {/* Add Customer Form */}
          {isFormVisible && (
            <div className="form-container">
              <h3>{isEditMode ? `‚úèÔ∏è Edit Customer: ${editingEntry?.name}` : 'Add New Customer'}</h3>
              <form className="customer-form" onSubmit={handleSubmit}>
                <div className="form-row">
                  <div className="form-group">
                    <label>üë§ Customer Name *</label>
                    <input
                      type="text"
                      name="name"
                      placeholder="Enter full name"
                      value={form.name}
                      onChange={handleChange}
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>üìû Phone Number *</label>
                    <input
                      type="tel"
                      name="number"
                      placeholder="Enter phone number"
                      value={form.number}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group full-width">
                    <label>üìß Email Address *</label>
                    <input
                      type="email"
                      name="email"
                      placeholder="Enter email address"
                      value={form.email}
                      onChange={handleChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-row">
                  <div className="form-group">
                    <label>üí∞ Amount to Pay *</label>
                    <input
                      type="number"
                      name="amountToPay"
                      placeholder="0.00"
                      value={form.amountToPay}
                      onChange={handleChange}
                      min="0"
                      step="0.01"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>üí≥ Amount Paid</label>
                    <input
                      type="number"
                      name="amountPaid"
                      placeholder="0.00"
                      value={form.amountPaid}
                      onChange={handleChange}
                      min="0"
                      step="0.01"
                    />
                  </div>
                </div>
                <div className="form-actions">
                  <button type="submit" className="submit-btn">üíæ Save Customer</button>
                  <button type="button" onClick={clearForm} className="clear-btn">üóëÔ∏è Clear Form</button>
                </div>
              </form>
            </div>
          )}

          {/* Customer Table */}
          <div className="table-container">
            <div className="table-header">
              <h3>Customer Records</h3>
              <span className="record-count">
                {filteredEntries.length} of {entries.length} customers
                {searchTerm && ` (filtered by "${searchTerm}")`}
              </span>
            </div>

            {loading ? (
              <div className="loading-state">
                <div className="loading-spinner"></div>
                <p>Loading customers...</p>
              </div>
            ) : filteredEntries.length === 0 ? (
              <div className="empty-state">
                {searchTerm ? (
                  <>
                    <p>üîç No customers found matching "{searchTerm}"</p>
                    <button onClick={() => setSearchTerm('')} className="clear-search-btn">
                      Clear Search
                    </button>
                  </>
                ) : (
                  <>
                    <p>üìã No customers added yet</p>
                    <button onClick={() => setIsFormVisible(true)} className="add-first-btn">
                      Add Your First Customer
                    </button>
                  </>
                )}
              </div>
            ) : (
              <div className="table-wrapper">
                <table className="customers-table">
                  <thead>
                    <tr>
                      <th>Customer Info</th>
                      <th>Contact</th>
                      <th>Amount to Pay</th>
                      <th>Amount Paid</th>
                      <th>Pending</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredEntries.map((entry) => {
                      const pending = entry.amountToPay - entry.amountPaid;
                      return (
                        <tr key={entry.id || entry.email} className={pending <= 0 ? 'paid-row' : 'pending-row'}>
                          <td>
                            <div className="customer-info">
                              <strong>{entry.name}</strong>
                            </div>
                          </td>
                          <td>
                            <div className="contact-info">
                              <div>üìû {entry.number}</div>
                              <div>üìß {entry.email}</div>
                            </div>
                          </td>
                          <td className="amount-cell">{formatCurrency(entry.amountToPay)}</td>
                          <td className="amount-cell">{formatCurrency(entry.amountPaid)}</td>
                          <td className={`amount-cell ${pending > 0 ? 'pending-amount' : 'paid-amount'}`}>
                            {formatCurrency(pending)}
                          </td>
                          <td>{getStatusBadge(entry.amountToPay, entry.amountPaid)}</td>
                          <td>
                            <button 
                              onClick={() => handleEdit(entry)}
                              className="edit-btn"
                              title="Edit customer"
                            >
                              ‚úèÔ∏è Edit
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
